var Carousel=function(e,t){this.imageNames=e;this.images=[];this.container=t;this.numberOfImages=e.length;this.currentImageIndex=0;for(var n=1;n<=this.numberOfImages;n++){this.images.push(new Image);this.images[n-1].src=this.imageNames[n-1]}};Carousel.prototype.showImage=function(){this.container.style.background="url("+this.imageNames[this.currentImageIndex]+")"};Carousel.prototype.forwards=function(){this.currentImageIndex++;if(this.currentImageIndex>this.numberOfImages-1)this.currentImageIndex=0;if(!this.numberOfImages)return;this.showImage()};Carousel.prototype.backwards=function(){this.currentImageIndex--;if(this.currentImageIndex<0)this.currentImageIndex=this.numberOfImages-1;if(!this.numberOfImages)return;this.showImage()}
